<?phpinclude_once ("../includes/funciones.inc.php");$idVot = $_SESSION['idVoto'];$clase= '';// Obtenemos la votacion$votos = 0; //calcular$valor = 0; //calcular$resVot = consulta ("select * from VOTACIONES where ID='$idVot'");if (numero_filas($resVot)==0) {	consulta ("insert into VOTACIONES (ID, VOTOS, VALOR) VALUES ('$idVot', '0', '0')");} else {	$resVot = extraer_registro($resVot);	$valor = $resVot['VALOR'];	$votos = $resVot['VOTOS'];}if ($votos>0 && $valor>0) {	$valor = number_format($valor / $votos, 0);}?><br/><div>	<div class="ui-stars-star-votos" style="position: relative; top: -5px;">	Votos: <?=$votos?>	<form style="float: right;" id="puntuacioncontenido<?=$idVot?>" method="post" action="grabarVotacion.php">		<div id="votacion<?=$idVot?>">			<select name="valor" >				<option value="1" <?php if ($valor==1) { ?>selected<?php } ?>>1</option>				<option value="2" <?php if ($valor==2) { ?>selected<?php } ?>>2</option>				<option value="3" <?php if ($valor==3) { ?>selected<?php } ?>>3</option>				<option value="4" <?php if ($valor==4) { ?>selected<?php } ?>>4</option>				<option value="5" <?php if ($valor==5) { ?>selected<?php } ?>>5</option>				<option value="6" <?php if ($valor==6) { ?>selected<?php } ?>>6</option>				<option value="7" <?php if ($valor==7) { ?>selected<?php } ?>>7</option>				<option value="8" <?php if ($valor==8) { ?>selected<?php } ?>>8</option>				<option value="9" <?php if ($valor==9) { ?>selected<?php } ?>>9</option>				<option value="10" <?php if ($valor==10) { ?>selected<?php } ?>>10</option>			</select>			<input type="hidden" name="id" value="<?=$idVot?>">		</div>	</form>	<script>	$("#puntuacioncontenido<?=$idVot?>").stars({		inputType : "select",		split : 2,		oneVoteOnly : true,		callback : function(ui, type, value) {			$.post("grabarVotacion.php?id=<?=$idVot?>&valor="+value, {}, function(data)			{				$("#ajax_response<?=$idVot?>").html(data);			});		}	});	</script>	<p id="ajax_response<?=$idVot?>" style="float: right;"></p>	</div></div> 